import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { LineChart, Line, CartesianGrid, XAxis, YAxis } from 'recharts';
import Select from 'react-select';

function ReservoirData(props) {

    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [chartRange, setChartRange] = useState('1 year');

    const options = [
        { value: '6 months', label: '6 months' },
        { value: '1 year', label: '1 year' },
        { value: '2 years', label: '2 years' }
    ]

    const customStyles = {
        control: (provided) => ({
            ...provided,
            fontSize: '16px', // Adjust the text size as needed
            height: '20px',  // Adjust the height as needed
            width: '150px'
        }),
        menu: (provided) => ({
            ...provided,
            fontSize: '16px',
            width: '150px',
        })
    };

    function formatDateString(dateString) {
        const date = new Date(dateString)
        return date.toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'numeric',
            day: 'numeric',
        })
    }

    function updateRange(newRange) {
        setChartRange(newRange.value);
        console.log(newRange.value);
    }

    function getDateRange() {
        const currentDate = new Date();
        let year = currentDate.getFullYear();
        let month = String(currentDate.getMonth() + 1).padStart(2, '0'); // Months are zero-based
        let day = String(currentDate.getDate()).padStart(2, '0');
        let endDate = `${year}-${month}-${day}`;
        // console.log(chartRange);
        year = year - 2;
        let startDate = `${year}-${month}-${day}`;
        return `Start=${startDate}&End=${endDate}`
    }

    function cleanData(data) {
        //format the dates
        data = data.map(item => ({
            stationId: item.stationId,
            value: item.value,
            date: formatDateString(item.date)
        }));

        //need to remove last element of data if data is sampled monthly, since the API returns 0 for value of current month
        if (chartRange === '1 year' || chartRange === '2 years') {
            data.pop();
        }

        //interpolating missing values (missing values are represented as -9999 by the API)
        for (let i = 0; i < data.length; i++) {
            if (data[i].value === -9999) {
                //if the missing value is the first value in the array, we cannot use the previous value for interpolation.
                //thus, we use the slope of the next two values
                if (i == 0) {
                    data[i].value = data[i + 1].value - (data[i + 2].value - data[i + 1].value)
                }
                //if the missing value is the last value in the array, we cannot use the next value for interpolation.
                //thus, we use the slope of the previous two values
                else if (i == data.length - 1) {
                    data[i].value = data[i - 1].value + (data[i - 1].value - data[i - 2].value)
                }
                //if the missing value has values before and after it, we simply take the average of these two values.
                else {
                    data[i].value = (data[i - 1].value + data[i + 1].value) / 2
                }
            }
        }
        return data
    }

    useEffect(() => {
        // console.log(getDateRange());
        // const url = `/dynamicapp/req/JSONDataServlet?Stations=${props.id}&SensorNums=15&dur_code=${(chartRange === '6 months' ? 'D' : 'M')}&${getDateRange()}`;
        // axios.get(url)
        //     .then(response => {
        //         setData(cleanData(response.data));
        //         // console.log(data);
        //         setLoading(false);
        //     })
        //     .catch(error => {
        //         console.log(error)
        //         setError(error);
        //         setLoading(false);
        //     })





        const url2 = `http://localhost:3000/resdata`;
        axios.get(url2)
            .then(response => {
                console.log(response.data);
                setLoading(false);
            })
            .catch(error => {
                console.log(error)
                setError(error);
                setLoading(false);
            })


    }, [])

    const renderLineChart = (
        <LineChart width={650} height={400} data={data.slice(0, 10)} margin={{ top: 20, right: 20, left: 50, bottom: 30 }}>
            <Line type="monotone" dataKey="value" stroke="#8884d8" isAnimationActive={false} />
            <CartesianGrid stroke="#ccc" />
            <XAxis label={{ dy: 25, fontSize: 20 }} dataKey="date" interval={5} tick={{ fontSize: 12 }} />
            <YAxis label={{ value: "Current Storage (AF)", dx: -60, angle: -90, fontSize: 20 }} tick={{ fontSize: 12 }} />
        </LineChart>
    );


    if (loading) {
        return <div>
            <h1>
                Loading...
            </h1>
        </div>;
    }

    if (error) {
        return <p>Error: {error.message}</p>;
    }

    return (
        <div className="ReservoirCard">
            <h3>
                {props.name}
                <div className="chart-container">
                    {renderLineChart}
                    <Select className="right-justified-select" options={options} styles={customStyles} fontSize='12' defaultValue={options[1]} onChange={updateRange} />

                </div>
            </h3>
        </div>
    )
}

export default ReservoirData;
