import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { LineChart, Line, CartesianGrid, XAxis, YAxis } from 'recharts';
import Select from 'react-select';

function ReservoirData(props) {

    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [sampleFreq, setSampleFreq] = useState('M');
    const [chartRange, setChartRange] = useState('1 year');

    const options = [
        { value: '6 months', label: '6 months' },
        { value: '1 year', label: '1 year' },
        { value: '2 years', label: '2 years' }
    ]

    const customStyles = {
        control: (provided) => ({
            ...provided,
            fontSize: '16px', // Adjust the text size as needed
            height: '20px',  // Adjust the height as needed
            width: '150px'
        }),
        menu: (provided) => ({
            ...provided,
            fontSize: '16px',
            width: '150px',
        })
    };

    function formatDateString(dateString) {
        const date = new Date(dateString)
        return date.toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'numeric',
            day: 'numeric',
        })
    }

    const formattedData = data.map(item => ({
        stationId: item.stationId,
        value: item.value,
        date: formatDateString(item.date)
    }))

    function updateRange(newRange) {
        setChartRange(newRange);
        console.log(chartRange);
    }

    useEffect(() => {
        const currentDate = new Date();
        const year = currentDate.getFullYear();
        const month = String(currentDate.getMonth() + 1).padStart(2, '0'); // Months are zero-based
        const day = String(currentDate.getDate()).padStart(2, '0');

        const url = `/dynamicapp/req/JSONDataServlet?Stations=${props.id}&SensorNums=15&dur_code=${sampleFreq}&Start=2020-07-24&End=${year}-${month}-${day}`;
        axios.get(url)
            .then(response => {
                let newData = response.data.map(item => ({
                    stationId: item.stationId,
                    value: item.value,
                    date: formatDateString(item.date)
                }));
                //need to remove last element of data if data is monthly, since the API returns 0 for value of current month
                if (sampleFreq == 'M') {
                    newData.pop();
                }
                setData(newData);
                console.log(data);
                setLoading(false);
            })
            .catch(error => {
                console.log(error)
                setError(error);
                setLoading(false);
            })
    }, [chartRange])

    const renderLineChart = (
        <LineChart width={650} height={400} data={formattedData} margin={{ top: 20, right: 20, left: 50, bottom: 30 }}>
            <Line type="monotone" dataKey="value" stroke="#8884d8" isAnimationActive={false} />
            <CartesianGrid stroke="#ccc" />
            <XAxis label={{ dy: 25, fontSize: 20 }} dataKey="date" interval={5} tick={{ fontSize: 12 }} />
            <YAxis label={{ value: "Current Storage (AF)", dx: -60, angle: -90, fontSize: 20 }} tick={{ fontSize: 12 }} />
        </LineChart>
    );


    if (loading) {
        return <div>
            <h1>
                Loading...
            </h1>
        </div>;
    }

    if (error) {
        return <p>Error: {error.message}</p>;
    }

    return (
        <div className="ReservoirCard">
            <h3>
                {props.name}
                <div className="chart-container">
                    {renderLineChart}
                    <Select className="right-justified-select" options={options} styles={customStyles} fontSize='12' defaultValue={options[1]} onChange={updateRange} />

                </div>
            </h3>
        </div>
    )
}

export default ReservoirData;
