import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { LineChart, Line, CartesianGrid, XAxis, YAxis } from 'recharts';

function ReservoirData(props) {

    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const url = `/dynamicapp/req/JSONDataServlet?Stations=${props.id}&SensorNums=15&dur_code=D&Start=2021-07-24&End=2023-08-24`;
        axios.get(url)
            .then(response => {
                setData(response.data);
                console.log(data);
                setLoading(false);
            })
            .catch(error => {
                console.log(error)
                setError(error);
                setLoading(false);
            })
    }, [])

    function formatDateString(dateString) {
        const date = new Date(dateString)
        return date.toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'numeric',
            day: 'numeric',
        })
    }

    const formattedData = data.map(item => ({
        stationId: item.stationId,
        value: item.value,
        date: formatDateString(item.date)
    }))

    console.log(formattedData);

    const renderLineChart = (
        <LineChart width={650} height={400} data={formattedData} margin={{ top: 20, right: 20, left: 50, bottom: 30 }}>
            <Line type="monotone" dataKey="value" stroke="#8884d8" />
            <CartesianGrid stroke="#ccc" />
            <XAxis label={{ value: "Date", dy: 25, fontSize: 20 }} dataKey="date" interval={70} tick={{ fontSize: 12 }} />
            <YAxis label={{ value: "Current Storage (AF)", dx: -60, angle: -90, fontSize: 20 }} tick={{ fontSize: 12 }} />
        </LineChart>
    );


    if (loading) {
        return <div>
            <h1>
                Loading...
            </h1>
        </div>;
    }

    if (error) {
        return <p>Error: {error.message}</p>;
    }

    return (
        <div className="ReservoirCard">
            <h3>
                {props.name}
                {renderLineChart}

                {/* <div>
                    <ul>
                        {data.map(item => (
                            <li >{item.value}</li>
                        ))}
                    </ul>
                </div> */}

            </h3>
        </div>
    )
}

export default ReservoirData;
